import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import axios from 'axios';
import './App.css';

function App() {
  return (
    <Router>
      <div className="app">
        <Navbar />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About our project />} />
          <Route path="/contact" element={<Contact us/>} />
          <Route path="/recent" element={<RecentChecks />} />
        </Routes>
        <Footer />
      </div>
    </Router>
  );
}

function Navbar() {
  return (
    <nav className="navbar">
      <div className="container">
        <Link to="/" className="brand">PHISHING DETECTOR</Link>
        <div className="nav-links">
          <Link to="/">Home</Link>
          <Link to="/about">About</Link>
          <Link to="/contact">Contact</Link>
          <Link to="/recent">Recent Checks</Link>
        </div>
      </div>
    </nav>
  );
}

function Home() {
  const [url, setUrl] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const checkUrl = async (e) => {
    e.preventDefault();
    if (!url) {
      setError('Please enter a URL to check');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await axios.post('http://localhost:5000/check', { url });

      if (response.data.error) {
        throw new Error(response.data.error);
      }

      setResult(response.data);

      // Save to recent checks in localStorage (max 10)
      const recent = JSON.parse(localStorage.getItem('recentChecks') || '[]');
      localStorage.setItem(
        'recentChecks',
        JSON.stringify([response.data, ...recent.filter(r => r.url !== response.data.url)].slice(0, 10))
      );

    } catch (err) {
      console.error("Error checking URL:", err);
      setError(err.response?.data?.error || err.message || 'Failed to check URL. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="home">
      <h1>Phishing Detection Website</h1>
      <div className="card">
        <form onSubmit={checkUrl}>
          <input
            type="url"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            placeholder="Enter URL (e.g., https://example.com)"
            required
          />
          <button type="submit" disabled={loading}>
            {loading ? (
              <>
                <span className="spinner"></span> Checking...
              </>
            ) : (
              'Check URL'
            )}
          </button>
        </form>

        {error && <div className="error">{error}</div>}

        {result && (
          <div className="result">
            <h3>Analysis Result</h3>
            <p>
              URL: <a href={result.url} target="_blank" rel="noopener noreferrer">{result.url}</a>
            </p>
            <div className={`status ${result.is_phishing ? 'phishing' : 'safe'}`}>
              {result.is_phishing ? '‚ö†Ô∏è PHISHING DETECTED' : '‚úÖ SAFE WEBSITE'}
            </div>
            <div className="risk-level">
              <span>Risk Level:</span>
              <div className="progress-bar">
                <div
                  className="progress"
                  style={{ width: `${Math.round(result.probability * 100)}%` }}
                >
                  {Math.round(result.probability * 100)}%
                </div>
              </div>
            </div>
            <p className="timestamp">Checked on: {result.timestamp}</p>

            {result.is_phishing && (
              <div className="warning">
                <p>‚ö†Ô∏è Warning: This website appears to be a phishing attempt.</p>
                <p>Do not enter any personal or sensitive information.</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

function About() {
  return (
    <div className="page">
      <h2>About the Phishing Detection System</h2>
      <p>
        The Phishing Detection Website is a smart and responsive web-based application designed to help users and institutions in Rwanda and beyond detect suspicious and potentially harmful websites. With the increasing number of online scams, phishing attacks, and fake websites mimicking trusted services like banks, government portals, and mobile money platforms, this tool acts as a first line of defense for everyday internet users.
      </p>

      <p>
        This system analyzes the structure of URLs using both machine learning algorithms and rule-based heuristics. It detects suspicious patterns like IP addresses used in URLs, misleading subdomains (e.g., <code>login.example-update.com</code>), the use of phishing-related keywords (e.g., <strong>login</strong>, <strong>verify</strong>, <strong>secure</strong>), and even subtle typosquatting attempts where attackers replace letters with numbers (like <code>yah00.com</code> instead of <code>yahoo.com</code>).
      </p>

      <p>
        The application is built using a hybrid approach:
        <ul>
          <li>‚úÖ <strong>Rule-based detection</strong> to quickly flag dangerous URLs with obvious phishing traits.</li>
          <li>ü§ñ <strong>Machine Learning (ML)</strong> to detect more subtle patterns learned from large datasets of legitimate and phishing websites.</li>
          <li>üìå <strong>Whitelisting</strong> for trusted Rwandan government and institutional domains like <code>gov.rw</code>, <code>rbc.gov.rw</code>, and <code>rra.gov.rw</code>.</li>
        </ul>
      </p>

      <p>
        Once a user inputs a URL, the system immediately extracts its features, evaluates its legitimacy, and provides a detailed result including risk level, phishing indicators, and a visual status (Safe ‚úÖ or Phishing ‚ö†Ô∏è). The results are saved locally as ‚ÄúRecent Checks‚Äù so the user can track past scans.
      </p>

      <p>
        This project is especially useful for Rwandan citizens, students, institutions, and developers who want to stay safe while accessing critical services like <strong>Irembo</strong>, <strong>REB</strong>, <strong>MoMo</strong>, <strong>RRA</strong>, <strong>MyICT</strong>, and many others.
      </p>
    </div>
  );
}

function Contact() {
  return (
    <div className="page">
      <h2>Contact</h2>
      <p>Telephone: 0789246013</p>
      <p>For inquiries, please email: <a href="mailto:rtuyi883@gmail.com">rtuyi883@gmail.com</a></p>
    </div>
  );
}

function RecentChecks() {
  const recent = JSON.parse(localStorage.getItem('recentChecks') || '[]');

  return (
    <div className="page">
      <h2>Recent Checks</h2>
      {recent.length === 0 ? (
        <p>No recent checks available.</p>
      ) : (
        <div style={{ maxHeight: '400px', overflowY: 'auto', border: '1px solid #ccc', borderRadius: '5px' }}>
          <table className="recent-table" style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th style={{ borderBottom: '2px solid #ccc', padding: '8px', textAlign: 'left' }}>Status</th>
                <th style={{ borderBottom: '2px solid #ccc', padding: '8px', textAlign: 'left' }}>URL</th>
                <th style={{ borderBottom: '2px solid #ccc', padding: '8px', textAlign: 'left' }}>Checked On</th>
              </tr>
            </thead>
            <tbody>
              {recent.map((item, index) => (
                <tr key={index}>
                  <td
                    style={{
                      color: item.is_phishing ? 'red' : 'green',
                      fontWeight: 'bold',
                      padding: '8px',
                      borderBottom: '1px solid #eee',
                      whiteSpace: 'nowrap',
                    }}
                  >
                    {item.is_phishing ? 'Phishing' : 'Legitimate'}
                  </td>
                  <td style={{ padding: '8px', borderBottom: '1px solid #eee', wordBreak: 'break-all' }}>
                    <a href={item.url} target="_blank" rel="noreferrer">
                      {item.url}
                    </a>
                  </td>
                  <td style={{ padding: '8px', borderBottom: '1px solid #eee', whiteSpace: 'nowrap' }}>
                    {item.timestamp}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}


function Footer() {
  return (
    <footer className="footer">
      <p>&copy; 2025 Phishing Detector. All rights reserved.</p>
    </footer>
  );
}

export default App;
