import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import axios from 'axios';
import './App.css';
import logo from './rp-logo.png';

function App() {
  return (
    <Router>
      <div className="app">
        <Navbar />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/recent" element={<RecentChecks />} />
        </Routes>
        <Footer />
      </div>
    </Router>
  );
}

function Navbar() {
  return (
    <nav className="navbar">
      <div className="container brand-container">
        <div className="brand-logo">
          <img src={logo} alt="RP Logo" className="logo" />
          <Link to="/" className="brand">PHISHING DETECTOR</Link>
        </div>
        <div className="nav-links">
          <Link to="/">Home</Link>
          <Link to="/about">About</Link>
          <Link to="/contact">Contact</Link>
          <Link to="/recent">Recent Checks</Link>
        </div>
      </div>
    </nav>
  );
}

function Home() {
  const [url, setUrl] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const checkUrl = async (e) => {
    e.preventDefault();
    if (!url) {
      setError('Please enter a URL to check');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await axios.post('http://localhost:5000/check', { url });

      if (response.data.error) throw new Error(response.data.error);
      setResult(response.data);

      const recent = JSON.parse(localStorage.getItem('recentChecks') || '[]');
      localStorage.setItem(
        'recentChecks',
        JSON.stringify([response.data, ...recent.filter(r => r.url !== response.data.url)].slice(0, 10))
      );
    } catch (err) {
      setError(err.response?.data?.error || err.message || 'Failed to check URL. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="home">
      <h1>Phishing Detector</h1>
      <div className="card">
        <form onSubmit={checkUrl}>
          <input
            type="url"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            placeholder="Enter URL (e.g., https://example.com)"
            required
          />
          <button type="submit" disabled={loading}>
            {loading ? <><span className="spinner"></span> Checking...</> : 'Check URL'}
          </button>
        </form>

        {error && <div className="error">{error}</div>}

        {result && (
          <div className="result">
            <h3>Analysis Result</h3>
            <p>URL: <a href={result.url} target="_blank" rel="noopener noreferrer">{result.url}</a></p>

            <div className={`status ${result.is_phishing ? 'phishing' : 'safe'}`}>
              {result.is_phishing ? '⚠️ PHISHING DETECTED' : '✅ SAFE WEBSITE'}
            </div>

            <div className="risk-level">
              <span>Risk Level:</span>
              <div className="progress-bar">
                <div className="progress" style={{ width: `${Math.round(result.probability * 100)}%` }}>
                  {Math.round(result.probability * 100)}%
                </div>
              </div>
            </div>

            <p className="timestamp">Checked on: {result.timestamp}</p>

            <p className="reason">{result.reason}</p>

            {result.is_phishing ? (
              <div className="warning">
                <p>⚠️ Do NOT trust or enter personal data.</p>
              </div>
            ) : (
              <div className="safe-box">
                <p>🔗 <a href={result.url} target="_blank" rel="noopener noreferrer">Visit Website</a></p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

