import React, { useState, useEffect } from 'react';
import './App.css';
import rpLogo from './rp-logo.png';

function App() {
  const [url, setUrl] = useState('');
  const [result, setResult] = useState(null); // Store full response object
  const [recentChecks, setRecentChecks] = useState([]);
  const [activeTab, setActiveTab] = useState('home');
  const [loading, setLoading] = useState(false);

  // === Fetch recent checks from backend ===
  const fetchRecent = async () => {
    try {
      const res = await fetch('http://localhost:5000/recent');
      if (!res.ok) throw new Error("Failed to fetch recent checks");
      const data = await res.json();
      setRecentChecks(data);
    } catch (error) {
      console.error("Error fetching recent checks:", error);
      setRecentChecks([]);
    }
  };

  useEffect(() => {
    fetchRecent();
  }, []);

  // === Handle form submission ===
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResult(null);
    try {
      const res = await fetch('http://localhost:5000/check', {  // Make sure your backend route is /check
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ url })
      });
      const data = await res.json();
      if (res.ok) {
        setResult(data);  // Store entire response object here
        fetchRecent();
      } else {
        setResult({ error: data.error || 'Unknown error' });
      }
    } catch (error) {
      console.error(error);
      setResult({ error: 'Server unreachable' });
    }
    setLoading(false);
  };

  return (
    <div className="App">
      {/* === Header Section === */}
      <header>
        <img src={rpLogo} alt="RP Logo" className="logo" />
        <h1>Phishing Detection Website</h1>
        <nav>
          <button onClick={() => setActiveTab('home')} className={activeTab === 'home' ? 'active' : ''}>Home</button>
          <button onClick={() => setActiveTab('about')} className={activeTab === 'about' ? 'active' : ''}>About Us</button>
          <button onClick={() => setActiveTab('contact')} className={activeTab === 'contact' ? 'active' : ''}>Contact Us</button>
          <button onClick={() => setActiveTab('recent')} className={activeTab === 'recent' ? 'active' : ''}>Recent Checks</button>
        </nav>
      </header>

      {/* === Main Content === */}
      <main>
        {activeTab === 'home' && (
          <>
            <form onSubmit={handleSubmit}>
              <input
                type="text"
                placeholder="Enter URL to check"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                required
              />
              <button type="submit" disabled={loading}>{loading ? 'Checking...' : 'Check URL'}</button>
            </form>

            {/* === Updated result display === */}
            {result && (
              <div className={`result ${result.is_phishing ? 'phishing' : 'safe'}`}>
                {result.error ? (
                  <p style={{ color: 'red' }}>Error: {result.error}</p>
                ) : (
                  <>
                    <p><strong>Analysis Result</strong></p>
                    <p>URL: {result.url}</p>
                    <p>{result.message}</p>
                    <p>Risk Level: {result.risk_level}</p>
                    <p>Checked on: {result.timestamp}</p>
                    <p>
                      <a href={result.url} target="_blank" rel="noopener noreferrer">Visit Website üîó</a>
                    </p>
                  </>
                )}
              </div>
            )}
          </>
        )}

        {activeTab === 'about' && (
          <section className="about">
            <h2>About Us</h2>
            <p>
              Our phishing detection platform uses a hybrid model combining <strong>machine learning</strong> and
              <strong> heuristic rules</strong> to assess the legitimacy of a website. We focus on providing safety
              particularly for <strong>Rwandan internet users</strong>, with special awareness of local domains
              and whitelisted government websites.
            </p>
            <p>
              This tool is designed by students of <strong>Rwanda Polytechnic</strong> to help reduce the risks of
              phishing attacks and online fraud by offering real-time detection of suspicious URLs.
            </p>
          </section>
        )}

        {activeTab === 'contact' && (
          <section className="contact">
            <h2>Contact Us</h2>
            <p><strong>üìû Phone:</strong> +250 789 260 13</p>
            <p><strong>üìß Email:</strong> rtuyi88@gmail.com</p>
            <p><strong>üïí Hours of Operation:</strong> Monday - Friday, 8:00 AM - 5:00 PM</p>
            <p><strong>üè¢ Address:</strong> Rwanda Polytechnic, Kigali Campus</p>
          </section>
        )}

        {activeTab === 'recent' && (
          <section className="recent-checks">
            <h2>Recent URL Checks</h2>
            <table>
              <thead>
                <tr>
                  <th>URL</th>
                  <th>Result</th>
                  <th>Checked At</th>
                </tr>
              </thead>
              <tbody>
                {recentChecks.length > 0 ? recentChecks.map((item, idx) => (
                  <tr key={idx}>
                    <td>{item.url || 'N/A'}</td>
                    <td className={item.prediction ? item.prediction.toLowerCase() : ''}>
                      {item.prediction || item.verdict || 'N/A'}
                    </td>
                    <td>{item.timestamp || item.time || 'N/A'}</td>
                  </tr>
                )) : (
                  <tr><td colSpan="3">No recent checks available.</td></tr>
                )}
              </tbody>
            </table>
          </section>
        )}
      </main>

      {/* === Footer === */}
      <footer>
        &copy; {new Date().getFullYear()} Rwanda Polytechnic. All rights reserved.
      </footer>
    </div>
  );
}

export default App;
